svg.script:
var c = document.getElementById('circle');
zoom();

function zoom() {
    if (!c.direction) {
        c.direction = "shrink";
        c.radius = parseInt(c.getAttribute('r'));
    } 

    c.radius += (c.direction === "grow")?0.5:-0.5;
    c.setAttribute('r', c.radius.toString());

    if (c.radius == 0)
        c.direction = "grow";
    if (c.radius == 125)
        c.direction = "shrink";

    requestAnimationFrame(zoom);
}
